// Fix file format for Groq API compatibility
const items = [];

for (const item of $input.all()) {
  // Get the binary data from Telegram
  const binaryData = item.binary?.data;

  if (binaryData) {
    // Set proper filename and mime type for Groq API
    binaryData.fileName = 'voice_message.ogg';
    binaryData.mimeType = 'audio/ogg';

    items.push({
      json: item.json,
      binary: {
        data: binaryData
      }
    });
  } else {
    items.push(item);
  }
}

return items;

// Extract and normalize message content
const items = [];

for (const item of $input.all()) {
  let messageText = '';
  let userId = '';
  let chatId = '';
  let messageId = '';

  // Handle different message types - check for body wrapper
  const message = item.json.body?.message || item.json.message;

  if (message) {
    userId = message.from.id;
    chatId = message.chat.id;
    messageId = message.message_id;

    // Text message
    if (message.text) {
      messageText = message.text;
    }
  }

  // Handle transcribed voice message from Groq API response
  if (item.json.text && !messageText) {
    messageText = item.json.text;
    // Get user info from the original webhook data if available
    const webhookData = $('Telegram Webhook').first()?.json;
    if (webhookData?.body?.message) {
      userId = webhookData.body.message.from.id;
      chatId = webhookData.body.message.chat.id;
      messageId = webhookData.body.message.message_id;
    }
  }

  items.push({
    json: {
      messageText: messageText,
      userId: userId,
      chatId: chatId,
      messageId: messageId,
      timestamp: new Date().toISOString(),
      originalMessage: item.json
    }
  });
}

return items;

// Prepare context for AI analysis
const items = [];

const normalizedMessage = $('Normalize Message').item.json;
const conversationMemory = $('Get Conversation Memory').all();
const legalDocs = $('Get Legal Documents').all();

// Build conversation history
let conversationHistory = '';
if (conversationMemory.length > 0 && conversationMemory[0].json.conversation_history) {
  conversationHistory = conversationMemory[0].json.conversation_history;
}

// Build document context
let documentContext = '';
if (legalDocs.length > 0) {
  documentContext = legalDocs.map(doc => {
    return `Document: ${doc.json.title}\nType: ${doc.json.type}\nContent: ${doc.json.content}\n---`;
  }).join('\n');
}

// Create AI prompt
const aiPrompt = `You are a legal AI assistant specializing in criminal law and legal matters. You excel at analyzing both direct legal questions and criminal scenarios/fact patterns.

When analyzing scenarios (like "a wife smashes her husband's phone"), identify ALL potential legal violations, charges, statutes, and case precedents that could apply. Consider different jurisdictions and varying circumstances.

For direct legal questions, provide comprehensive explanations with relevant statutes and case law.

CONVERSATION HISTORY:
${conversationHistory}

CURRENT QUESTION/SCENARIO:
${normalizedMessage.messageText}

LEGAL DOCUMENTS CONTEXT:
${documentContext}

Please provide your response in the following format:

**SHORT SUMMARY:**
[Brief summary of relevant statutes/laws/cases that apply to this scenario]

**DETAILED BREAKDOWN:**
[For each potential violation/legal issue:
‚Ä¢ Specific statute or law
‚Ä¢ Elements that must be proven
‚Ä¢ How the scenario meets (or doesn't meet) those elements
‚Ä¢ Potential penalties/consequences
‚Ä¢ Relevant case precedents]

**CONCLUSION:**
[Summary of most likely charges/violations, defenses, and recommended next steps]

For scenarios, analyze from multiple angles: criminal charges, civil liability, defenses, and jurisdictional variations. Be thorough and consider various interpretations of the facts.`;

items.push({
  json: {
    aiPrompt: aiPrompt,
    userMessage: normalizedMessage.messageText,
    userId: normalizedMessage.userId,
    chatId: normalizedMessage.chatId,
    conversationHistory: conversationHistory,
    timestamp: normalizedMessage.timestamp
  }
});

return items;

