// Format AI response and update conversation history
const items = [];

// Get AI response from Groq format
const aiResponse = $json.choices && $json.choices[0] && $json.choices[0].message && $json.choices[0].message.content || 'Unable to generate response';
const context = $('Prepare AI Context').item.json;

// Update conversation history
const newConversationEntry = `User: ${context.userMessage}\nAssistant: ${aiResponse}\n---\n`;
const updatedHistory = context.conversationHistory + newConversationEntry;

// Format response for Telegram
let formattedResponse = aiResponse
  .replace(/\*\*(.*?)\*\*/g, '*$1*')  // Convert ** to * for Telegram
  .replace(/###/g, 'üî∏')  // Replace ### with bullet points
  .replace(/##/g, 'üìã');  // Replace ## with icons

// Telegram message limit is 4096 characters
const MAX_LENGTH = 4000; // Leave some buffer

if (formattedResponse.length > MAX_LENGTH) {
  // Try to find a good break point (end of sentence, paragraph, or section)
  let truncateAt = MAX_LENGTH;
  const breakPoints = ['. ', '\n\n', '\n**', '\n‚Ä¢'];

  for (const breakPoint of breakPoints) {
    const lastBreak = formattedResponse.lastIndexOf(breakPoint, MAX_LENGTH - 100);
    if (lastBreak > MAX_LENGTH / 2) {
      truncateAt = lastBreak + breakPoint.length;
      break;
    }
  }

  formattedResponse = formattedResponse.substring(0, truncateAt) + '\n\nüìù *Response truncated due to length. Ask follow-up questions for more details.*';
}

items.push({
  json: {
    formattedResponse: formattedResponse,
    userId: context.userId,
    chatId: context.chatId,
    updatedConversationHistory: updatedHistory,
    timestamp: context.timestamp,
    rawAiResponse: aiResponse
  }
});

return items;
